import React, {Component} from 'react';

class Form extends Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: '',
      age: '',
      dica: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({target}) {
    const { name, value} = target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { name, email, age, dica} = this.state;

    return (
      <div>
        <h1>Formulariozin</h1>
        <form className="form"> 

        <label htmlFor="name">
          Nome:  
          <input 
          id="name" 
          name="name" 
          type="text"
          onChange= {this.handleChange}
          value={name}
          />
        </label>

        <label htmlFor="email">
          Email:
          <input 
          id="email" 
          name="email" 
          type="email"
          onChange={ this.handleChange }
          value={ email }
        />
        </label>

        <label htmlFor="age">
          Idade:
          <select 
          id="age" 
          name="name" 
          defaultValue=" "
          onChange={this.handleChange}
          value
          >
          <option value=''>Selecione</option>
          <option value="adulto">+18</option>
          <option value="underage">-18</option>
          </select>
        </label>


        </form>
      </div>
    )
  }
}


export default Form;
