import React, {Component} from 'react';

class Form extends Component {
  constructor() {
    super();

    this.state = {
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({
      email: event.target.value,
    });
  }

  render() {
    const {email} = this.state;

    return (
      <div>
        <h1>Formulariozin</h1>
        <form className="form"> 
        <label htmlFor="name">
          Nome:  
          <input
          id="name"
          name="name"
          type="text"
          />
        </label>

        <label htmlFor="email">
          Email:
          <input
          id="email"
          name="email"
          type="email"
          onChange={ this.handleChange }
          value={ email }
        />
        </label>

        <label htmlFor="age">
          Idade: 
          <select
          id="age"
          name="name"
          defaultValue=" "
          >
          <option value=''>Selecione</option>
          <option value="adulto">Selecione</option>
          <option value="underage">Selecione</option>

          </select>

        </label>


        </form>
      </div>
    )
  }
}


export default Form;
